import {
  auth_data as auth_store
} from "auth/store";
let auth_data;
auth_store.subscribe(x => auth_data = x)

function send({
  method,
  path,
  data,
}) {
  const fetch = process.browser ? window.fetch : require('node-fetch').default;

  const opts = {
    method,
    headers: {},
    credentials: 'include'
  };

  opts.headers['X-CSRF-Token'] = auth_data.csrf_token;

  if (data) {
    opts.headers['Content-Type'] = 'application/json';
    opts.body = JSON.stringify(data);
  }

  return fetch(`${path}`, opts)
    .then(r => r.text())
    .then(json => {
      try {
        return JSON.parse(json);
      } catch (err) {
        return json;
      }
    });
}

export function get(path) {
  return send({
    method: 'GET',
    path
  });
}

export function del(path) {
  return send({
    method: 'DELETE',
    path
  });
}

export function post(path, data) {
  return send({
    method: 'POST',
    path,
    data
  });
}

export function put(path, data) {
  return send({
    method: 'PUT',
    path,
    data
  });
}